import { selectPage } from "Features/store/pageSlice";
import { useSelector } from "react-redux";
import styled from "styled-components";
// import { useDispatch } from "react-redux";
// import { nextPage, previousPage } from "../../Features/store/pageSlice";

const isActive = useSelector(selectPage);

export const MenuConteiner = styled.div`
  font-family: "TTFirsNeue400";
  margin: 50px auto 19px;
  width: 647px;
  height: 17px;
  display: flex;
  justify-content: space-between;
  @media (max-width: 768px) {
    display: none;
  }
`;

export const ButtonConteiner = styled.div`
  font-size: 14px;
  align-items: center;
  display: flex;
  cursor: pointer;
  line-height: 1.2em;
  color: #fefefe;
`;
