import styled from "styled-components";
import "./App.css";
import CompanyPage from "./Pages/copmany/CompanyPage";
import PersonPage from "./Pages/person/PersonPage";
import Footer from "./Widgets/footer/Footer";
import HeaderMenu from "./Widgets/header/HeaderMenu";

import { useSelector } from "react-redux";
import { selectPage } from "./Features/store/pageSlice";

const MainConteiner = styled.div`
  margin: 0px auto;
  width: 647px;
  height: 664px;
  background-color: #f4f4f4;
  border-radius: 8px;
  padding-top: 32px;
  @media (max-width: 798px) {
    height: 100%;
  }
`;
const MobileCopmanyPAgeActivate = styled.div`
  display: none;
  @media (max-width: 768px) {
    display: block;
  }
`;

function App() {
  const isActivePage = useSelector(selectPage);

  return (
    <div className="App">
      <HeaderMenu />
      <MainConteiner>
        {isActivePage ? <PersonPage /> : <CompanyPage />}
        <MobileCopmanyPAgeActivate>
          <Footer />
          <CompanyPage />
        </MobileCopmanyPAgeActivate>
        <Footer />
      </MainConteiner>
    </div>
  );
}

export default App;
