import styled from "styled-components";

export const PersonConteiner = styled.div`
  padding-left: 24px;
  /* @media (max-width: 768px) {
    border-radius: 8.11px;
    box-shadow: 0px 4.05px 5.68px 0px rgba(0, 0, 0, 0.03);
    margin: 44px auto;
    background-color: rgba(254, 254, 254, 1);
    height: 100%;
    padding-left: 0px;
    margin-top: -9vh;
  } */
`;

export const LinkConteoner = styled.div`
  margin-top: 27px;
  display: block;

  @media (max-width: 768px) {
    /* margin-top: 12px;
    margin-bottom: 12px;
    margin-left: 20px;
    height: 100%;
    padding-bottom: 16px;
    padding-top: 14px; */
  }
`;
export const Link = styled.a`
  display: flex;
  align-items: center;
  margin-bottom: 19px;
  font-family: "TTFirsNeue400";
  text-decoration: none;
  color: black;
`;

export const Name = styled.div`
  font-family: "TTFirsNeueBold";
  font-size: 20px;
  margin-top: 5px;
  @media (max-width: 768px) {
    margin-left: 21px;
  }
`;

export const Surname = styled.div`
  font-family: "TTFirsNeueBold";
  font-size: 20px;
  margin-top: 23px;
  @media (max-width: 768px) {
    margin-left: 21px;
  }
`;

export const Position = styled.div`
  font-family: "TTFirsNeue400";
  font-size: 14px;
  color: #455773;
  margin-top: 15px;
  @media (max-width: 768px) {
    margin-left: 21px;
  }
`;
