import { createSlice } from "@reduxjs/toolkit";

const initialState = {
  activePage: "true",
};

const PageSlice = createSlice({
  name: "Page",
  initialState,
  reducers: {
    nextPage: (state) => {
      state.activePage = "false";
    },
  },
});

export const { nextPage } = PageSlice.actions;

export default PageSlice.reducer;
