import styled from "styled-components";

export const MainConteiner = styled.div`
  display: grid;
`;

export const Conteiner = styled.div`
  background-color: red;
  height: 561px;
  display: block;
`;

export const SimpleSliderConteiner = styled.div`
  overflow: hidden;
  margin-left: 32px;
  width: 279px;
  height: 495px;
  border-radius: 8.11px;
  @media (max-width: 768px) {
    max-height: 400px;
    margin: 20vw auto;
    width: 100%;
  }
`;
export const BtnDesktop = styled.button`
  font-family: "TTFirsNeueBold";
  font-size: 12px;
  color: #fefefe;
  text-align: center;
  background-color: black;
  border-radius: 8.11px;
  width: 279px;
  height: 50px;
  margin-left: 32px;
  margin-top: 16px;
  text-transform: uppercase;
  cursor: pointer;
  @media (max-width: 768px) {
    display: none;
  }
`;

export const VectorTop = styled.div`
  margin-top: 84px;
  width: 647px;
  height: 1px;
  border-bottom: solid 1.5px #060606;
  @media (max-width: 768px) {
    display: none;
  }
`;
