import React from "react";
import SimpleSlider from "Pages/person/photo/PhotoSlider/SimpleSlider";
import {
  PhotoConteiner,
  SimpleSliderConteiner,
  AddBtn,
  ButtonMobile,
  BtnConteiner,
} from "./style";
import { Icon } from "Shared/svg/Icon";
import { vcardContent } from "Features/VCARD";
import saveVCard from "Features/saveVCard";
import Data from "Entites/Data";

export default function Photo() {
  const { name, main_namber, mail, address } = Data;
  function saveVCard(Data) {
    const vCardContent = `
      BEGIN:VCARD
      VERSION:3.0
      FN:${name}
      TEL;TYPE=CELL:${main_namber}
      EMAIL:${mail}
      ADR:${address}
      END:VCARD
    `;
    const blob = new Blob([vCardContent], { type: "text/vcard" });
    saveAs(blob, "contact.vcf");
  }
  return (
    <PhotoConteiner>
      <SimpleSliderConteiner>
        <SimpleSlider />
      </SimpleSliderConteiner>
      <AddBtn onClick={() => saveVCard()}>Добавить в контакты</AddBtn>
      <BtnConteiner>
        <ButtonMobile>
          <Icon id="save" className="photo-btn" />
          сохранить визитку
        </ButtonMobile>
        <ButtonMobile>
          <Icon id="call" className="photo-btn" />
          позвонить
        </ButtonMobile>
      </BtnConteiner>
    </PhotoConteiner>
  );
}
