import { createSlice } from "@reduxjs/toolkit";

const PageSlice = createSlice({
  name: "Page",
  initialState: {
    activePage: "false",
  },
  reducers: {
    nextPage(store, action) {
      activePage("true");
    },
  },
});

export const { nextPage } = PageSlice.actions;

export default PageSlice.reducer;
