import CompanyPage from "./Pages/copmany/CompanyPage";
import PersonPage from "./Pages/person/PersonPage";
import Footer from "./Widgets/footer/Footer";
import HeaderMenu from "./Widgets/header/HeaderMenu";
import { MainConteiner } from "AppStyle";
import { useSelector } from "react-redux";
import { selectPage } from "./Features/store/pageSlice";
import Mobile from "MobileVersion/Mobile";
import { useResize } from "Features/hooks/useResize";

function App() {
  const isActivePage = useSelector(selectPage);
  const activePage = isActivePage ? <PersonPage /> : <CompanyPage />;
  const { width } = useResize();
  const mobileWidth = width <= 768;
  const desktopWidth = width > 768;

  return (
    <div className="App">
      {desktopWidth && (
        <>
          <HeaderMenu />
          <MainConteiner>
            {activePage}
            <Footer activePage={isActivePage} />
          </MainConteiner>
        </>
      )}
      {mobileWidth && <Mobile />}
    </div>
  );
}

export default App;
