import Data from "Entites/Data";

const { nane, surname, patronymic, main_number, mail, address } = Data.person;
const saveVCard = (Data) => {
  const fullName = `${nane}+ ${surname}+ ${patronymic}`;
  const vCardContent = `
      BEGIN:VCARD
      VERSION:3.0
      FN:${fullName}
      TEL;TYPE=CELL:${main_number}
      EMAIL:${mail}
      ADR:${address}
      END:VCARD
    `;
  const blob = new Blob([vCardContent], { type: "text/vcard" });
  saveAs(blob, "contact.vcf");
};

export default saveVCard;
